function OpenBusinessStorage()
    ESX.TriggerServerCallback("iG_BusinessStorage:getBusinessStorageInventory", function(inventory)
            TriggerEvent("ig-inventory:openBusinessStorageInventory", inventory)
    end, ESX.PlayerData.job.name)
end


function OpenJobActionsMenu()

	ESX.UI.Menu.CloseAll()

	ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'job_actions_menu', {
		title    = "Job Actions Menu",
		align    = 'right',
		elements = {
			{label = "Billing",       value = 'billing'},
		}
	}, function(data, menu)
		if isBusy then return end

		if data.current.value == 'billing' then

			ESX.UI.Menu.Open('dialog', GetCurrentResourceName(), 'billing', {
				title = "Enter amount to invoice closest player:"
			}, function(data, menu)
				local amount = tonumber(data.value)

				if amount == nil or amount < 0 then
					ESX.ShowNotification("The amount you have entered is invalid.")
				else
					local closestPlayer, closestDistance = ESX.Game.GetClosestPlayer()
					if closestPlayer == -1 or closestDistance > 3.0 then
						ESX.ShowNotification("There are no players nearby.")
					else
						menu.close()
						TriggerServerEvent('iG_billing:sendBill', GetPlayerServerId(closestPlayer), 'society_vanillaunicorn', "Vanilla Unicorn", amount)
						-- if Config.EnableJobLogs == true then
						-- 	TriggerServerEvent('igLogs:AddInLog', "casino", "sendBill", GetPlayerName(PlayerId()), GetPlayerName(closestPlayer), "The Diamond Casino", amount)
						-- end
					end
				end
			end, function(data, menu)
				menu.close()
			end)
		end
	end, function(data, menu)
		menu.close()
	end)
end





